    call printar_highlight
    .jogo:
        call ler_letra

        cmp al, 'd'
        je .direita

        cmp al, 'a'
        je .esquerda

        cmp al, 13
        je .escolhenum
        
        cmp al, 'f'
        je .verifica

        cmp al, 8
        je .backspace

        jmp .jogo

        .direita:
            mov ah, byte[qtd]
            cmp byte[count], ah
            je .jogo
            add byte[count], 1
            mov si, hollow
            push di
            call printar_tabuleiro
            pop di
            
            inc di
            mov al, [di]
            
            mov byte[counth], al
            call printar_highlight
            jmp .jogo
        .esquerda:
            cmp byte[count], 0
            je .jogo
            add byte[count], -1 

            
            mov si, hollow
            push di
            call printar_tabuleiro
            pop di
            
            dec di
            mov al, [di]
           
            mov byte[counth], al
            call printar_highlight
            jmp .jogo
        .escolhenum:
            call ler_letra

            cmp al, '1'
            jl .escolhenum
            cmp al, '9'
            jg .escolhenum

            mov ah, 02h
            int 10h

            mov bl, byte[counth]
            mov si, hollow
            .aumenta:
                cmp bl, 0
                je .movido
                inc si
                dec bl
                jmp .aumenta
            .movido:
            mov [si], al
            mov si, hollow
            push di
            call printar_tabuleiro
            pop di
            jmp .jogo
        .verifica:
